package eu.scape_project.tool.workflow_uploader;

import javax.ws.rs.client.Client;
import javax.ws.rs.core.Cookie;
import javax.ws.rs.core.MediaType;
import javax.ws.rs.core.NewCookie;

import org.apache.commons.cli.Option;
import org.apache.commons.cli.Options;
import org.glassfish.jersey.client.ClientResponse;

public class WorkflowUploader {

	// private static Logger log = Logger.getLogger(WorkflowUploader.class);
	private Client restClient;
	private Options options;
	private static final String postValue = "<?xml version=\"1.0\"?><workflow><title>My workflow</title><description>My workflow.</description><license-type>by-sa</license-type><content-type>application/xaml+xml</content-type><content encoding=\"base64\" type=\"binary\"></content><preview encoding=\"base64\" type=\"binary\"></preview></workflow>";
	private static final String DEFAULT_MY_EXPERIMENT_BASE_URL = "http://www.myexperiment.org";
	private static final String DEFAULT_MY_EXPERIMENT_SESSION_COOKIE_NAME = "myexperiment_session";
	private String myExperimentBaseURL;

	public WorkflowUploader() {
		myExperimentBaseURL = DEFAULT_MY_EXPERIMENT_BASE_URL;

		// REST client instantiation and configuration
		restClient = Client.create();
		restClient.setFollowRedirects(true);

		Option opt;
		options = new Options();
		opt = new Option("w", "workflow", true, "workflow file location");
		opt.setRequired(true);
		options.addOption(opt);
		opt = new Option("o", "outDir", true,
				"directory where to put the generated artifacts");
		opt.setRequired(true);
		options.addOption(opt);
	}

	public WebResource getWebResource(String resourceLocation) {
		return restClient.resource(resourceLocation);
	}

	public String getMyExperimentSessionID(String username, String password) {
		String res = null;
		WebResource webResourceForCookieRequest = getWebResource(myExperimentBaseURL
				+ "/session");
		String cookieRequestData = "<session><username>" + username
				+ "</username><password>" + password + "</password></session>";
		ClientResponse clientResponse = webResourceForCookieRequest.entity(
				cookieRequestData, MediaType.APPLICATION_XML).post(
				ClientResponse.class);
		for (NewCookie cookie : clientResponse.getCookies()) {
			if (DEFAULT_MY_EXPERIMENT_SESSION_COOKIE_NAME.equals(cookie
					.getName())) {
				res = cookie.getValue();
				break;
			}
		}
		return res;
	}

	public static void main(String[] args) {
		// obtain object instance
		WorkflowUploader workflowUploader = new WorkflowUploader();

		// authenticate user on myExperiment
		String myExperimentSessionID = workflowUploader
				.getMyExperimentSessionID("hsilva", "9u11pcu4");

		if (myExperimentSessionID != null) {
			// if user was successfully authenticated on myExperiment

			WebResource webResource = workflowUploader
					.getWebResource(DEFAULT_MY_EXPERIMENT_BASE_URL
							+ "/workflow.xml");

			Cookie authenticationCookie = new Cookie(
					DEFAULT_MY_EXPERIMENT_SESSION_COOKIE_NAME,
					myExperimentSessionID);

			String responseString = webResource.cookie(authenticationCookie)
					.accept(MediaType.APPLICATION_XML)
					.type(MediaType.APPLICATION_XML)
					.post(String.class, postValue);
			System.out.println(responseString);
		}

		// WebResource webResource = workflowUploader
		// .getWebResource("http://sandbox.myexperiment.org/workflows.xml");
		// String responseString = webResource.accept(
		// MediaType.APPLICATION_XML_TYPE).get(String.class);
		// System.out.println(responseString);

	}
}
